@import url('https://fonts.googleapis.com/css2?family=Dancing+Script:wght@600&display=swap');


.homePageWrapper {
    display: flex;
    align-items: center;
    justify-content: center;
    color: #fff;
    height: 100vh;
}

.formWrapper {
    background: #282a3679;
    padding: 20px;
    border-radius: 10px;
    width: 400px;
    max-width: 70%;
}

.inputGroup {
    display: flex;
    flex-direction: column;
}

.mainLabel {
    margin-bottom: 20px;
    margin-top: 0;
}

.homePageLogo {
    font-size: 50px;
    text-align: center;
    background: -webkit-linear-gradient(aqua, rgb(47, 189, 189));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    font-family: 'Dancing Script', cursive;
    height: 80px;
    margin-bottom: 30px;
}

.inputBox {
    padding: 10px;
    border-radius: 5px;
    outline: none;
    border: none;
    margin-bottom: 14px;
    background: #eee;
    font-size: 16px;
    font-weight: bold;
}

.btn {
    border: none;
    padding: 10px;
    border-radius: 5px;
    font-size: 16px;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease-in-out;
}

.joinBtn,
.leaveBtn {
    background: aqua;
    width: 100px;
    margin-left: auto;
}

.joinBtn:hover,
.leaveBtn:hover {
    background: rgb(15, 174, 174);
    ;
}

.createInfo {
    margin: 0 auto;
    margin-top: 20px;
}

.createNewBtn {
    color: aqua;
    text-decoration: none;
    border-bottom: 1px solid aqua;
    transition: all 0.3s ease-in-out;
}

.createNewBtn:hover {
    color: rgb(15, 174, 174);
    border-color: rgb(15, 174, 174);
}

.mainWrap {
    display: grid;
    background-color:#ffffff;
    grid-template-columns: 1fr 1fr;
    height: 100vh;
}

.aside {
   /*background: linear-gradient(135deg, #333233, #080408);
    padding: 16px;
    color: #fff;*/
    display: flex;
    flex-direction: column;
}

.asideInner {
    flex: 1;
}

.clientsList {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    gap: 20px;
}

.client {
    display: flex;
    align-items: center;
    flex-direction: column;
    font-weight: bold;
}

.userName {
    margin-top: 10px;
}

.logo {
    background: -webkit-linear-gradient(aqua, rgb(47, 189, 189));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    font-size: 20px;
    text-align: center;
    font-family: 'Dancing Script', cursive;
    border-bottom: 1px solid #424242;
    padding-bottom: 10px;
}


.leaveBtn {
    width: 100%;
    margin-top: 20px;
}

.CodeMirror {
    /*min-height: calc(100vh - 20px);*/
    font-size: 20px;
    line-height: 1.6;
    padding-top: 0px;
    /* was 20px*/
}

.editorWrap {
    display: grid;
    grid-template-rows: 80% 20%;
    height: 100vh;
}

.terminal {
    background-color: black;
    height: 100%;
}
